          }
        }
      }
    },
    delMitem: function delMitem() {
      var _this = this;

      // 1:创建变量保存多个购物车中id值
      var str = ""; // 2：创建循环遍历数组获取状态为true的id值

      var _iteratorNormalCompletion2 = true;
      var _didIteratorError2 = false;
      var _iteratorError2 = undefined;

      try {
        for (var _iterator2 = this.list[Symbol.iterator](), _step2; !(_iteratorNormalCompletion2 = (_step2 = _iterator2.next()).done); _iteratorNormalCompletion2 = true) {
          var item = _step2.value;

          if (item.cb) {
            str += item.id + ",";
          }
        } //console.log(str.length);

      } catch (err) {
        _didIteratorError2 = true;
        _iteratorError2 = err;
      } finally {
        try {
          if (!_iteratorNormalCompletion2 && _iterator2.return != null) {
            _iterator2.return();
          }
        } finally {
          if (_didIteratorError2) {
            throw _iteratorError2;
          }
        }
      }

      if (str.length == 0) {
        this.$toast("未选中任何商品！");
      } else {
        // 3：截取字符串中最后的逗号
        str = str.substring(0, str.length - 1); //console.log(str)   1,2,3
        // 4：显示交互提示框，再次请用户确认操作

        this.$messagebox.confirm("是否删除数据 ？").then(function (res) {
          //用户选择确认按钮
          var url = "delM";
          var ids = {
            ids: str
          }; //参数名字ids:值拼接字符串

          _this.axios.get(url, {
            params: ids
          }).then(function (res) {
            _this.loadMore(); //删除多个商品后，加载新的购物车列表


            _this.$toast("删除成功");
          });
        }).catch(function (err) {
          console.log(err);
        }); // 5：发送ajax请求删除多个数据
        // 6：提示用户删除成功
        //7:重新加载购物车中数据
      }
    },
    cartjiayi: function cartjiayi(e) {
      var _this2 = this;

      var lid = e.target.dataset.lid;
      var n = e.target.dataset.lname;
      var p = e.target.dataset.price;
      console.log(lid + ":" + n + ":" + p);
      var url = "addcart";
      var obj = {
        lid: lid,
        lname: n,
        price: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == -1) {
          _this2.$toast("请先登录再添加商品");
        } else {
          _this2.$toast("添加成功");

          _this2.loadMore();
        }
      });
    },
