      var _this3 = this;

      var lid = e.target.dataset.lid;
      var n = e.target.dataset.lname;
      var p = e.target.dataset.price;
      console.log(lid + ":" + n + ":" + p);
      var url = "cartjianyi";
      var obj = {
        lid: lid,
        lname: n,
        price: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == -1) {
          _this3.$toast("已是最小数量！");
        } else {
          _this3.$toast("您删除了一件商品！");

          _this3.loadMore();
        }
      });
    },
    loadMore: function loadMore() {
      var _this4 = this;

      var url = "cart";
      this.axios.get(url).then(function (res) {
        //console.log(res);
        //交互：如果用户未登录就想查看购物车，提示请先登录并跳转登录页面
        if (res.data.code == -1) {
          _this4.$messagebox("消息", "请登录").then(function (res) {
            _this4.$router.push("/Login");

            return;
          });
        } else {
          var list = res.data.data; //console.log(list);
          //为了实现购物车多选删除在此创建循环遍历数组并且为每个元素添加cb属性表示，商品前复选框状态
          //注意先添加cb属性再赋值list  顺序很重要
          //刷新购物车商品数量时为防止重复清点，应先清空后再加载 

          _this4.$store.commit("clearItem");

          var _iteratorNormalCompletion3 = true;
          var _didIteratorError3 = false;
          var _iteratorError3 = undefined;

          try {
            for (var _iterator3 = list[Symbol.iterator](), _step3; !(_iteratorNormalCompletion3 = (_step3 = _iterator3.next()).done); _iteratorNormalCompletion3 = true) {
              var item = _step3.value;
              item.cb = false;

              _this4.$store.commit("addItem"); //修改共享购物车中的数值Vuex

            }
          } catch (err) {
            _didIteratorError3 = true;
            _iteratorError3 = err;
          } finally {
            try {
              if (!_iteratorNormalCompletion3 && _iterator3.return != null) {
                _iterator3.return();
              }
            } finally {
              if (_didIteratorError3) {
                throw _iteratorError3;
              }
            }
          }

          _this4.list = list;
        }
      });
    },
    delItem: function delItem(e) {
      var _this5 = this;

      var id = e.target.dataset.id; // console.log(id);

      this.$messagebox.confirm("是否删除指定商品").then(function (res) {
        //同意
        //发送ajax服务器
        var url = "del";
        var obj = {
          id: id
        };

        _this5.axios.get(url, {
          params: obj
        }).then(function (res) {
          if (res.data.code > 0) {
            _this5.$toast("删除成功");

            _this5.loadMore();
          }
        });
      }).catch(function (err) {
        console.log("用户取消操作");
      });
    }
  },
  created: function created() {
    this.loadMore();
  }
};
